import React from "react"
import api from "../utils/Api"
import closeAllPopups from "../components/App"

const Main = (props) => {

  const [ userName, setUserName ] = React.useState(null)
  const [ userDescription, setUserDescription ] = React.useState(null)
  const [ userAvatar, setUserAvatar ] = React.useState(null)
  const [ currentUser, setCurrentUser ] = React.useState(null)
  const [ cards, setCards ] = React.useState([])

    React.useEffect(() => {
      const fetchData = async () => {
        try {
          const [ userInfoRes, cardsInfoRes ] = await Promise.all([
            api.getUser(),
            api.getCard()
          ])
          setCurrentUser({
            name: userInfoRes.name,
            about: userInfoRes.about,
            avatar: userInfoRes.avatar,
            id: userInfoRes._id
          })
          setCards(cardsInfoRes)
        } catch ( err ) {
          console.log(err)
        }
      }
      fetchData()
      console.log(currentUser)
    }, [])

  const handleUpdateAvatar = async ({ avatar }) => {
    try {
      const updatedUser = await api.updateUserAvatar({ avatar })
      setCurrentUser({
        name: updatedUser.name,
        about: updatedUser.about,
        avatar: updatedUser.avatar,
        id: updatedUser._id
      })
      closeAllPopups()
    } catch ( err ) {
      console.error(err)
    }
  }

  console.log()
  return (
    <main className="content">

      <section className="profile">

        <div className="profile__avatar-container">
          <button onClick={ props.onEditAvatar } className="profile__avatar-edd"></button>
          <img className="profile__avatar" alt="Аватар профиля"/>
        </div>

        <div className="profile__info">
          <h1 className="profile__name"></h1>
          <button onClick={ props.onEditProfile } type="button"
                  className="profile__edit-button profile__edit-button-link"></button>
          <p className="profile__about"></p>
        </div>

        <button onClick={ props.onAddPlace } type="button" className="profile__add-button"></button>

      </section>

      <section className="elements">

      </section>

    </main>
  )
}

export default Main
