import React from "react"
import api from "../utils/Api"
const Main = (props) => {

  const [userName, setUserName] = React.useState(null)
  const [userDescription, setUserDescription] = React.useState(null)
  const [userAvatar, setUserAvatar] = React.useState(null)

  React.useEffect(() => {
    api.getUser().then(r => console.log(r))
  }, [userName, userDescription, userAvatar])

  return (
    <main className="content">

      <section className="profile">

        <div className="profile__avatar-container">
          <button onClick={props.onEditAvatar} className="profile__avatar-edd"></button>
          <img className="profile__avatar" src="#" alt="Аватар профиля"/>
        </div>

        <div className="profile__info">
          <h1 className="profile__name"></h1>
          <button onClick={props.onEditProfile} type="button" className="profile__edit-button profile__edit-button-link"></button>
          <p className="profile__about"></p>
        </div>

        <button onClick={props.onAddPlace} type="button" className="profile__add-button"></button>

      </section>

      <section className="elements">

      </section>

    </main>
  )
}

export default Main
